import React, { Component } from 'react';
import './assets/style.css';
import api from './api/Questions';

class App extends Component {
  state = {

    questionBank: [] // Local state to store questions once pulled from api
  };
  getQuestions = () => {
    api().then(question => {
      this.setState({
        questionBank: question
      });
    });
  };
  componentDidMount() {
    this.getQuestions();
  }
  render() {
    return (
      <div className="container">
        <header className="title"><h2>U.S. Civics Test Prep</h2></header>
        {this.state.questionBank.map(({question, answers, correct}) => <h4>{question}</h4>)}
      </div>
    );
  }
}

export default App;
