import React, { Component } from 'react';
import './assets/style.css';
import api from './api/QuestionBank';
import Questions from './components/Questions'

class App extends Component {
  state = {
    questionBank: [] // Local state to store questions once pulled from api
  };
  getQuestions = () => {
    api().then(question => {
      // update state variable
      this.setState({
        questionBank: question 
      });
    });
  };
  componentDidMount() {
    this.getQuestions();
  }
  render() {
    return (
      <div className="container">
        <header className="title"><h2>U.S. Civics Test Prep</h2></header>
        {this.state.questionBank.map(({question, answers, correct, questionId}) => <QuestionBox question = {question} options={answers}key={questionId} /> )}
      </div>
    );
  }
}

export default App;
