import React, { Component } from 'react';
import qBank from './quizData'

class Quiz extends Component {
  constructor() {
    super()
    this.state = {
      answer: null,
      currentQuestion: 0,
      options: [],
      end: false,
      score: 0,
      disabled: true
    }
    this.handleClick = this.handleClick.bind(this);
    this.checkAnswer = this.checkAnswer.bind(this);
    this.finishQuiz = this.finishQuiz.bind(this);

  }

  loadQuiz = () => {
    this.setState(() => {
      return {
        questions: qBank[this.state.currentQuestion].question,
        options: qBank[this.state.currentQuestion].options,
        answers: qBank[this.state.currentQuestion].answer
      }
    })
  };

  componentDidMount() {
    this.loadQuiz()
  }

  handleClick = () => {
    const {answers, userAnswer, score} = this.state;
    this.setState({
      currentQuestion: this.state.currentQuestion + 1
    })

    console.log(this.state.currentQuestion)
    if(userAnswer === answers) {
      this.setState({
        score: score + 1
      })
    }
  }

  // updates component
  componentDidUpdate(prevProps, prevState) {
    if (this.state.currentQuestion !== prevState.currentQuestion) {
      this.setState(() => {
        return {
          disabled: true,
          questions: qBank[this.state.currentQuestion].question,
          options: qBank[this.state.currentQuestion].options,
          answers: qBank[this.state.currentQuestion].answer
        }
      })
    }
  }

  checkAnswer = userAnswer => {
    this.setState({
      answer: userAnswer,
      disabled: false
    })
  }

  finishQuiz = () => {
    if (this.state.currentQuestion === qBank.length - 1) {
      this.setState({
        end: true
      })
    }
  }

  render() {
    const { questions, options, answer, currentQuestion, end } = this.state;

    if (end) {
      return <div>
        <h2>
          Game Over! <br/> Your final score is {this.state.score}
        </h2>
        <p>The Correct Answers for the Questions were: </p>
        <ul>
          {qBank.map((item, index) => (
            <li className="ui floating message options" key={index}>{item.answer}</li>
          ))}
        </ul>
      </div>
    }
    return (
      <div className="App">
        <h2>{questions}</h2>
        <span>{`Questions ${currentQuestion} out of ${qBank.length - 1}`}</span>
        {options.map(option => (
          <p key={option.id} onClick={() => this.checkAnswer(option)} className={`ui floating message options ${answer === option ? "selected" : null}`}>
            {option}
          </p>
        ))}
        {currentQuestion < qBank.length - 1 &&
          <button disabled={this.state.disabled} onClick={this.handleClick}>Next</button>}
        {currentQuestion === qBank.length - 1 &&
          <button onClick={this.finishQuiz}>Finish</button>}
      </div>
    );
  }
}

export default Quiz;
